import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
    FaShoppingCart, 
    FaApple, 
    FaEgg, 
    FaCookie, 
    FaCarrot 
} from 'react-icons/fa';
import { IoSearch } from 'react-icons/io5';
import './css/Home.css';

const BASE_URL = 'http://localhost:5000/api';

const categories = [
    { 
        name: 'Vegetables', 
        icon: <FaCarrot color="#4CAF50" size={48} />,
        query: 'Vegetables'
    },
    { 
        name: 'Fruits', 
        icon: <FaApple color="#4CAF50" size={48} />,
        query: 'Fruits'
    },
    { 
        name: 'Dairy & Eggs', 
        icon: <FaEgg color="#4CAF50" size={48} />,
        query: 'Dairy'
    },
    { 
        name: 'Bakery', 
        icon: <FaCookie color="#4CAF50" size={48} />,
        query: 'Bakery'
    }
];

function Home() {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [cart, setCart] = useState([]);

    useEffect(() => {
        const getProducts = async () => {
            try {
                const response = await axios.get(`${BASE_URL}/products/get-product`, {
                    params: { 
                        limit: 100 
                    }
                });
                setProducts(response.data.products || []);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching products:', error);
                setError(error.response?.data?.message || 'Failed to fetch products');
                setLoading(false);
            }
        };

        getProducts();
    }, []);

    const handleSearch = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`${BASE_URL}/get-product`, {
                params: { 
                    search: searchTerm 
                }
            });
            setProducts(response.data.products || []);
            setLoading(false);
        } catch (error) {
            console.error('Error searching products:', error);
            setError(error.response?.data?.message || 'Failed to search products');
            setLoading(false);
        }
    };

    const handleCategoryClick = async (categoryQuery) => {
        try {
            setLoading(true);
            const response = await axios.get(`${BASE_URL}/get-product`, {
                params: { 
                    category: categoryQuery 
                }
            });
            setProducts(response.data.products || []);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching category products:', error);
            setError(error.response?.data?.message || 'Failed to fetch category products');
            setLoading(false);
        }
    };

    const handleAddToCart = (product) => {
        const existingItem = cart.find(item => item._id === product._id);
        
        if (existingItem) {
            setCart(cart.map(item => 
                item._id === product._id 
                    ? {...item, quantity: item.quantity + 1} 
                    : item
            ));
        } else {
            setCart([...cart, {...product, quantity: 1}]);
        }
    };

    const handleRemoveFromCart = (productId) => {
        setCart(cart.filter(item => item._id !== productId));
    };

    return (
        <>
            <header>
                <section className="hero">
                    <h1 className="hero-text">Fresh & Organic Products<br/>Delivered to Your Door</h1>
                    <div className="search-bar">
                        <input 
                            type="text" 
                            placeholder="Search for fresh products..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <button onClick={handleSearch}>
                            <IoSearch size={20} />
                            Search
                        </button>
                    </div>
                </section>
            </header>

            <section className="categories">
                <h2>Shop by Category</h2>
                <div className="category-container">
                    {categories.map((category, index) => (
                        <div 
                            key={index} 
                            className={`category-box ${category.name.toLowerCase().replace(' & ', '-')}`}
                            onClick={() => handleCategoryClick(category.query)}
                        >
                            {category.icon}
                            <span>{category.name}</span>
                        </div>
                    ))}
                </div>
            </section>

            <section className="featured">
                <h2>All Products</h2>
                {loading ? (
                    <div className="loading">Loading products...</div>
                ) : error ? (
                    <div className="error">{error}</div>
                ) : products.length === 0 ? (
                    <div className="no-products">No products found</div>
                ) : (
                    <div className="product-container">
                        {products.map((product) => (
                            <div key={product._id} className="product-card">
                                <img 
                                    src={product.imageUrl || '/placeholder-product.jpg'} 
                                    alt={product.name}
                                />
                                <h3>{product.name}</h3>
                                <p>{product.description}</p>
                                <div className="groups">
                                    <span className="price">${product.price.toFixed(2)}</span>
                                    <button 
                                        className="add-btn"
                                        onClick={() => handleAddToCart(product)}
                                    >
                                        <FaShoppingCart size={16} />
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </section>

            {/* Cart Section */}
            <section className="cart">
                <h2>Your Cart</h2>
                {cart.length === 0 ? (
                    <p>Your cart is empty</p>
                ) : (
                    <div className="cart-items">
                        {cart.map(item => (
                            <div key={item._id} className="cart-item">
                                <img 
                                    src={item.imageUrl || '/placeholder-product.jpg'} 
                                    alt={item.name}
                                />
                                <div>
                                    <h3>{item.name}</h3>
                                    <p>Quantity: {item.quantity}</p>
                                    <p>Price: ${(item.price * item.quantity).toFixed(2)}</p>
                                    <button onClick={() => handleRemoveFromCart(item._id)}>
                                        Remove
                                    </button>
                                </div>
                            </div>
                        ))};
                    </div>
                )};
            </section>
        </>
    );
}

export default Home;
