import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './css/Home.css';

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  // Categories data
  const categories = [
    { name: 'Vegetables', slug: 'vegetables', image: 'vegetables-image.jpg' },
    { name: 'Fruits', slug: 'fruits', image: 'fruits-image.jpg' },
    { name: 'Bakery', slug: 'bakery', image: 'bakery-image.jpg' },
    { name: 'Dairy', slug: 'dairy', image: 'dairy-image.jpg' }
  ];

  // Fetch products
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/products/get-product');
      setProducts(response.data.products);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching products:', error);
      setLoading(false);
    }
  };

  // Search products
  const handleSearch = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/products/get-product', {
        params: { search: searchTerm }
      });
      setProducts(response.data.products);
      setLoading(false);
    } catch (error) {
      console.error('Error searching products:', error);
      setLoading(false);
    }
  };

  // Add to cart handler
  const handleAddToCart = (product) => {
    console.log('Added to cart:', product);
    // Implement actual cart logic
  };

  return (
    <div>
      {/* Banner Section */}
      <div className="banner-section">
        <div className="banner-overlay"></div>
        <div className="search-container">
          <input 
            type="text" 
            className="search-input"
            placeholder="Search products..." 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button 
            className="search-button"
            onClick={handleSearch}
          >
            Search
          </button>
        </div>
      </div>

      {/* Category Section */}
      <section className="category-section">
        <div className="container">
          <h2 className="section-title">Shop by Category</h2>
          <div className="category-grid">
            {categories.map((category) => (
              <Link 
                key={category.slug} 
                to={`/category/${category.slug}`} 
                className="category-card"
              >
                <img 
                  src={category.image} 
                  alt={category.name} 
                  className="category-image"
                />
                <div className="category-title">{category.name}</div>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Products Section */}
      <section className="products-section">
        <div className="container">
          <h2 className="section-title">Our Products</h2>
          
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
            </div>
          ) : products.length === 0 ? (
            <div className="empty-state">
              No products found
            </div>
          ) : (
            <div className="products-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <img 
                    src={product.imageUrl} 
                    alt={product.name} 
                    className="product-image"
                  />
                  <div className="product-details">
                    <h3 className="product-name">{product.name}</h3>
                    <p className="product-description">{product.description}</p>
                    <div className="product-footer">
                      <span className="product-price">
                        ${product.price.toFixed(2)}
                      </span>
                      <button 
                        className="add-to-cart-btn"
                        onClick={() => handleAddToCart(product)}
                      >
                        Add to Cart
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
