import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './css/Home.css';

// Category component
const CategorySection = () => {
  const categories = [
    { name: 'Vegetables', slug: 'vegetables', image: '/api/placeholder/300/200?text=Vegetables' },
    { name: 'Fruits', slug: 'fruits', image: '/api/placeholder/300/200?text=Fruits' },
    { name: 'Bakery', slug: 'bakery', image: '/api/placeholder/300/200?text=Bakery' },
    { name: 'Dairy', slug: 'dairy', image: '/api/placeholder/300/200?text=Dairy' }
  ];

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-2xl font-bold mb-6 text-center">Shop by Category</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {categories.map((category) => (
          <Link 
            key={category.slug} 
            to={`/category/${category.slug}`} 
            className="hover:shadow-lg transition-all duration-300"
          >
            <div className="bg-white rounded-lg overflow-hidden">
              <img 
                src={category.image} 
                alt={category.name} 
                className="w-full h-48 object-cover"
              />
              <div className="p-4 text-center">
                <h3 className="font-semibold">{category.name}</h3>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

// Product Card Component
const ProductCard = ({ product, onAddToCart }) => {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <img 
        src={product.imageUrl || '/api/placeholder/300/300?text=Product'} 
        alt={product.name} 
        className="w-full h-48 object-cover"
      />
      <div className="p-4">
        <h3 className="font-semibold text-lg">{product.name}</h3>
        <p className="text-gray-600 mb-2">{product.description}</p>
        <div className="flex justify-between items-center">
          <span className="font-bold text-xl">${product.price.toFixed(2)}</span>
          <button 
            onClick={() => onAddToCart(product)} 
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

// Home Page Component
const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/api/product/get-product');
      setProducts(response.data.products);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get('/api/product/get-product', {
        params: { search: searchTerm }
      });
      setProducts(response.data.products);
    } catch (error) {
      console.error('Error searching products:', error);
    }
  };

  const handleAddToCart = (product) => {
    // Implement cart functionality
    console.log('Added to cart:', product);
    // You would typically dispatch an action to update cart state
  };

  return (
    <div>
      {/* Banner Section */}
      <div 
        className="h-[500px] bg-cover bg-center flex items-center justify-center"
        style={{ backgroundImage: 'url(/api/placeholder/1500/500?text=Fresh+Produce+Banner)' }}
      >
        <div className="bg-white bg-opacity-80 p-6 rounded-lg shadow-lg w-full max-w-lg">
          <div className="flex">
            <input 
              type="text" 
              placeholder="Search products..." 
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="flex-grow px-4 py-2 border rounded-l-lg"
            />
            <button 
              onClick={handleSearch}
              className="bg-green-500 text-white px-6 py-2 rounded-r-lg hover:bg-green-600 transition-colors"
            >
              Search
            </button>
          </div>
        </div>
      </div>

      {/* Category Section */}
      <CategorySection />

      {/* Products Section */}
      <div className="container mx-auto py-8">
        <h2 className="text-2xl font-bold mb-6 text-center">Our Products</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {products.map((product) => (
            <ProductCard 
              key={product._id} 
              product={product} 
              onAddToCart={handleAddToCart} 
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
