import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ShoppingCart, Apple, Egg, BreadSlice, Carrot } from 'lucide-react';
import './css/Home.css';

const categories = [
    { 
        name: 'Vegetables', 
        icon: <Carrot color="#4CAF50" size={48} />,
        query: 'vegetables'
    },
    { 
        name: 'Fruits', 
        icon: <Apple color="#4CAF50" size={48} />,
        query: 'fruits'
    },
    { 
        name: 'Dairy & Eggs', 
        icon: <Egg color="#4CAF50" size={48} />,
        query: 'dairy'
    },
    { 
        name: 'Bakery', 
        icon: <BreadSlice color="#4CAF50" size={48} />,
        query: 'bakery'
    }
];

export default function Home() {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchFeaturedProducts = async () => {
            try {
                const response = await axios.get('/api/products', {
                    params: { 
                        limit: 4, 
                        sort: 'featured' 
                    }
                });
                setProducts(response.data.products);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchFeaturedProducts();
    }, []);

    const handleSearch = async () => {
        try {
            const response = await axios.get('/api/products', {
                params: { 
                    search: searchTerm 
                }
            });
            setProducts(response.data.products);
        } catch (error) {
            console.error('Error searching products:', error);
        }
    };

    const handleCategoryClick = async (categoryQuery) => {
        try {
            const response = await axios.get('/api/products', {
                params: { 
                    category: categoryQuery 
                }
            });
            setProducts(response.data.products);
        } catch (error) {
            console.error('Error fetching category products:', error);
        }
    };

    return (
        <>
            <header>
                <section className="hero">
                    <h1 className="hero-text">Fresh & Organic Products<br/>Delivered to Your Door</h1>
                    <div className="search-bar">
                        <input 
                            type="text" 
                            placeholder="Search for fresh products..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <button onClick={handleSearch}>Search</button>
                    </div>
                </section>
            </header>

            <section className="categories">
                <h2>Shop by Category</h2>
                <div className="category-container">
                    {categories.map((category, index) => (
                        <div 
                            key={index} 
                            className={`category-box ${category.name.toLowerCase().replace(' & ', '-')}`}
                            onClick={() => handleCategoryClick(category.query)}
                        >
                            {category.icon}
                            <span>{category.name}</span>
                        </div>
                    ))}
                </div>
            </section>

            <section className="featured">
                <h2>Featured Products</h2>
                <div className="product-container">
                    {products.map((product) => (
                        <div key={product._id} className="product-card">
                            <img 
                                src={product.imageUrl || '/placeholder-product.jpg'} 
                                alt={product.name}
                            />
                            <h3>{product.name}</h3>
                            <p>{product.description}</p>
                            <div className="groups">
                                <span className="price">${product.price.toFixed(2)}</span>
                                <button className="add-btn">
                                    <ShoppingCart size={16} />
                                    Add to Cart
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </section>
        </>
    );
}
